package ch03_op;

public class Op_Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//부호연산자(+ , -) : + = 현재의 부호 유지 | - = 현재의 부호 변경 
		int x = -10;
		System.out.println("+x = " + +x);
		System.out.println("-x = " + -x);
		
		x = 10;
		System.out.println("+x = " + +x);
		System.out.println("-x = " + -x);
		
	}

}
