package ch03_op;

public class Op_Ex05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* / : 나누기 연산자(정수/정수 = int)
		 * % : 나머지 연산자(나머지 결과 값의 부호는 피젯수(나누어 지는 수)의 부호와 동일
		 */
		System.out.println(10/8);
		System.out.println(10/-8);
		System.out.println(-10/8);
		System.out.println(-10/-8);
		
		System.out.println("----------");
		
		System.out.println(10%8);
		System.out.println(10%-8);
		System.out.println(-10%8);
		System.out.println(-10%-8);
	}

}
